//main application where render everything core output (mothership)
import React, {Component} from "react";
import './App.css';
import Navbar from "./Navbar";
import Web3 from "web3";

class App extends Component{

    async Web3(){// check that eth open , aka the metamask is open
        if(Window.ethereum){
            window.web3 = new Web3.apply(window.ethereum)
            await window.ethereum.enable()
        }
    }
    //props part of react allow to parse over info
    constructor(props){
        super(props)
        this.state = {
            account: '0x0'
        }
    }


    // we parse the this.state.account to navbar
    //react code goes in here 
    render(){
        return(  //divs are just containers that can include styles, e.g text-centre comes from bootstrap
            <div>
                <Navbar account = {this.state.account}/>
                <div className='text-center'>
                    
                </div>
            </div>
        )
    }


}

export default App;


//class name invokes bootstrap styles seen in index.js

